import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom';
import axios from "axios";
export function Add(props) {

  const albums = props.albums;
  const navigate = useNavigate();
  const [albumData, setAlbumData] = useState({
    albumName: "",
    albumReview: "",
    albumRating: null,

  });
  function handleChange(event){
    setAlbumData(prevState=>(
      {...prevState, [event.target.name]: event.target.value}
    ))
  }; 
  function SearchAlbum(){
    useEffect(()=>{
      async function fetchSearchAlbums(){
        try{
          const res = await axios.get("http://localhost:8800/search/"+albumData.albumName)
          console.log(res.data);
        }
        catch(err){
          console.log(err)
        }
  
      }
      fetchSearchAlbums();
    }, [albumData.albumName])
  }


  async function handleClick(event){
    event.preventDefault();
    try{
      await axios.post("http://localhost:8800/albums", albumData);
      navigate("/");
    }
    catch(err){
      console.log("Error");
    }
    
  }

  return (
    <form onSubmit={handleClick}>
        <input onChange={handleChange} className="search-bar" type="text" placeholder="Enter album name..." name="albumName"></input>
        <button onClick={SearchAlbum}>Search Album!</button>

        <textarea placeholder='Write a review!' type="text" name="albumReview" onChange={handleChange}></textarea>
        <input type="number" placeholder="Out of 10" onChange={handleChange} name="albumRating"></input> 
        <button type="submit">Add album</button>
    </form>
  )
}
