import {Link} from "react-router-dom";
import { useEffect } from "react";
import axios from "axios";

export function Albums(props){
    const [selectedAlbum, setSelectedAlbum] = useState(null);
    const albums = props.albums;
    const setAlbums = props.setAlbums;
  useEffect(()=>{
    const fetchAllAlbums = async () =>{
        try{
            const res = await axios.get("http://localhost:8800/albums")
            setAlbums(res.data);
        }
        catch(err){
            console.log(err);
        }
    }
      fetchAllAlbums();
  },[setAlbums])

  const albumsMapped = albums.map((album, index)=>(
    <div className="album" key={album.id}>
        <div className="album-left">
            <img className="album-img" src={album.albumCover} alt=''/>
            <p className="album-rating">{album.albumRating}/10</p>
        </div>
        <div className="album-info-container">
            <div className="album-info">
                <h2 className="album-title">{album.albumName}</h2>
                <h3 className="album-artist">{album.albumArtist}</h3>
                <p className="album-review">{album.albumReview}</p>
            </div>
        </div>

    </div>
))
  return (
    <div className="album-page">
        <h1>Album review thingy</h1>
        <button className="add-button"><Link to="/add">Add new album</Link></button>
        <button className="update-button"><Link to="/update">Update album</Link></button>
        <div className="albums">
            {albumsMapped}
        </div>
    </div>

  )
}
