import React, { useEffect, useState } from 'react'
import Link from "react-router-dom";
import axios from "axios";
export const Albums = () => {
    const [albums, setAlbums] = useState([]);
    useEffect(()=>{
        const fetchAllAlbums = async () =>{
            try{
                const res = await axios.get("http://localhost:8800/albums")
                setAlbums(res.data);
            }
            catch(err){
                console.log(err);
            }
        }
        fetchAllAlbums();
    },[])



  return (
    <div>
        <h1>Jack's Album Reviews!</h1>
        <div className="albums">
            {albums.map((album)=>(
                <div className="album" key={album.id}>
                    <img src={album.albumCover} alt=''/>
                    <h2>{album.albumName}</h2>
                    <h3>{album.albumArtist}</h3>
                    <p>{album.albumReview}</p>
                    <span>{album.albumRating}</span>
                </div>
            ))}
        </div>
        <button><Link to></Link></button>
    </div>

  )
}
