import React, { useEffect } from 'react'
import { useState } from 'react'
import { useLocation } from 'react-router-dom';
import axios from 'axios';
export const Update = () => {
  const [album, setAlbum] = useState({});
  const location = useLocation();
  const albumId = location.pathname.split("/")[2];
  useEffect(()=>{
    async function fetchData(){
      const fetchedAlbum = await axios.get("http://localhost:8800/albums/" + albumId);
      setAlbum(fetchedAlbum.data[0]);
    }
    fetchData();

  }, [albumId])


  console.log(album);
  return (
    <div>
      Update Book
      <div className='album'>
        <div className='album-left'>  
          <img src={album.albumCover} alt=""></img>
          <div className='album'>
            <input type="number" value={album.albumRating}/>

          </div>
        </div>
        <div className='album-right'>
          <h2>{album.albumName}</h2>
          <h3>{album.albumArtist}</h3>
          <textarea value={album.albumReview}></textarea>

        </div>
      </div>

      
      
    </div>
  )
}
