import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom';
import axios from "axios";
import Searchbar from '../components/Searchbar';
import { useEffect } from 'react';
export const AlbumContext = React.createContext();
export function Add(props) {
  const albums = props.albums;
  const setAlbums = props.setAlbums;
  const navigate = useNavigate();

  const [albumData, setAlbumData] = useState({
    albumName: "",
    albumReview: "",
    albumRating: null,

  });
  function handleChange(event){
    setAlbumData(prevState=>(
      {...prevState, [event.target.name]: event.target.value}
    ))
  }; 

  useEffect(()=>{
    const fetchAllAlbums = async () =>{
      console.log("useeffect ran")
        try{
            const res = await axios.get("http://localhost:8800/albums")
            setAlbums(res.data);
        }
        catch(err){
            console.log(err);
        }
    }
      fetchAllAlbums();
  },[])
  async function handleClick(event){
    event.preventDefault();
    try{
      await axios.post("http://localhost:8800/albums", albumData);
      navigate("/");
    }
    catch(err){
      console.log("Error");
    }
    
  }

  return (
    <div>
        <Searchbar albums={albums}/>
        <input onChange={handleChange} className="search-bar" type="text" placeholder="Enter album name..." name="albumName"></input>
        <textarea placeholder='Write a review!' type="text" name="albumReview" onChange={handleChange}></textarea>
        <input type="number" placeholder="Out of 10" onChange={handleChange} name="albumRating"></input> 
        <button onClick={handleClick}>Add album</button>
    </div>
  )
}
