import './App.css';
import {Albums} from "./pages/Albums";
import {Update} from "./pages/Update";
import {Add} from "./pages/Add";
import {
  BrowserRouter,
  Routes,
  Route,
} from "react-router-dom";
function App() {
  return (
    <div className="App">
      <BrowserRouter>
        <Routes>
          <Route path="/" element={<Albums/>}/>
          <Route path="/add" element={<Add/>}/>
          <Route path="/update" element={<Update/>}/>

        </Routes>
      </BrowserRouter>
    </div>
  );
}
function authorize() {
  return new Promise((resolve, reject) => {  // this ensures i can return the token in the .then() 
    axios
      .post(authOptions.url, null, {
        headers: authOptions.headers,
        params: authOptions.form,
      })
      .then((response) => {
        const token = response.data.access_token;
        resolve(token); // Resolve the promise with the token
      })
      .catch((error) => {
        reject(error); // Reject the promise with the error
      });
  });
}

export async function searchForAlbum(albumName) {
  const BASE_URL = 'https://api.spotify.com/v1/search?q===';
  const query = `${albumName}&type=album&limit=1`;
  const header = {'Authorization': 'Bearer ' + token}
  try {
    const response = await axios.get(BASE_URL + query, {
      headers: header
    });
    console.log(response.data['albums']);
    console.log(response.data['albums']['items'][0]['artists'])
  } catch (error) {
    console.log("Error");
  }
}

export default App;
