import {Link} from "react-router-dom";
import { useEffect , useState} from "react";
import axios from "axios";

export function Albums(props){
const [selectedAlbum, setSelectedAlbum] = useState(null);
const albums = props.albums;
const setAlbums = props.setAlbums;
  useEffect(()=>{
    const fetchAllAlbums = async () =>{
        try{
            const res = await axios.get("http://localhost:8800/albums")
            setAlbums(res.data);
        }
        catch(err){
            console.log(err);
        }
    }
      fetchAllAlbums();
  },[setAlbums])
  
  const albumsMapped = albums.map((album, index)=>{
    const normalStyle = {
        display: 'flex',
        flexDirection: 'row',
        wordWrap: 'break-word',
        border: '2px solid #CCCCCC',
        borderRadius: '10px',
        padding: '10px',
        marginBottom: '10px',
        maxWidth: '50vw',
        cursor: 'pointer',
    }
    const selectedStyle = {...normalStyle, border: "2px solid black"} 
    
    return (
    <div style={(index === selectedAlbum) ? selectedStyle: normalStyle} key={album.id} onClick={()=>setSelectedAlbum(index)}>
        <div className="album-left">
            <img className="album-img" src={album.albumCover} alt=''/>
            <p className="album-rating">{album.albumRating}/10</p>
        </div>
        <div className="album-info-container">
            <div className="album-info">
                <h2 className="album-title">{album.albumName}</h2>
                <h3 className="album-artist">{album.albumArtist}</h3>
                <p className="album-review">{album.albumReview}</p>
            </div>
        </div>

    </div>
    )})


  return (
    <div className="album-page">
        <h1>Album review thingy</h1>
        <button className="add-button"><Link to="/add">Add new album</Link></button>
        {(selectedAlbum || selectedAlbum === 0)&& <button className="update-button"><Link to={`/update/${albums[selectedAlbum].id}`}>Update album</Link></button>}
        {(selectedAlbum || selectedAlbum === 0)&&<button>Delete</button>}
        <div className="albums">
            {albumsMapped}
        </div>
    </div>

  )
}
