import React, { useEffect, useState } from 'react'
import axios from "axios";
export const Albums = () => {
    const [albums, setAlbums] = useState([]);


    useEffect(()=>{
        const fetchAllAlbums = async () =>{
            try{
                const res = await axios.get("http://localhost:8800/albums")
                console.log(res);
            }
            catch(err){
                console.log(err);
            }
        }
        fetchAllAlbums();
    },[])
  return (
    <div>
        <h1>Jack's Album Reviews!</h1>
        <div className="albums">
            {albums.map(album=>(
                <div className="album" key={albums.id}>
                    <img src={albums.albumCover} alt=''/>
                    <h2>{albums.albumName}</h2>
                    <h3>{albums.albumArtist}</h3>
                    <p>{albums.albumReview}</p>
                    <span>{albums.rating}</span>
                </div>
            ))};
        </div>
    </div>

  )
}
