import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom';
import axios from "axios";
export function Add(props) {

  const navigate = useNavigate();
  const [albumData, setAlbumData] = useState({
    albumReview: "",
    albumRating: null,
  });
  const [searchAlbumState, setSearchAlbumState] = useState([]);
  const [selectedAlbum, setSelectedAlbum] = useState(null);
  function handleChange(event){
    setAlbumData(prevState=>(
      {...prevState, [event.target.name]: event.target.value}
    ))
  }; 
  const [selected, setSelected] = useState(false);

  async function displaySearchAlbums(){
    const albums = await searchAlbums();
    setSearchAlbumState(albums.items);

  }
  async function searchAlbums(){
    try{
      const res = await axios.get("http://localhost:8800/search/"+albumData.albumName)
      return res.data
    }
    catch(err){
      console.log(err)
    }

  }

  const albumsMapped = searchAlbumState.map((alb, index)=>{
    const styles = {
      border: selectedAlbum === index ? "5px solid" : "none" 
    };
    return(
      <div key={alb.id}>
        <h1>{alb.name}</h1>
        <img className="search-album-img" style={styles} src={alb.images[1].url} onClick={()=>setSelectedAlbum(index)}></img>
        <h3>{alb.release_date}</h3>
        <h3>{alb.type}</h3>
      </div>
    )
  })

  async function handleClick(event){
    event.preventDefault();
    try{
      const body = {
        album: searchAlbumState[selectedAlbum],
        user
      }
      await axios.post("http://localhost:8800/albums", albumData);
      navigate("/");
    }
    catch(err){
      console.log("Error");
    }
    
  }

  return (
    <form onSubmit={handleClick}>
        <input onChange={handleChange} className="search-bar" type="text" placeholder="Enter album name..." name="albumName"></input>
        <button type="button" onClick={displaySearchAlbums}>Search Album!</button>
        {albumsMapped}
        <textarea placeholder='Write a review!' type="text" name="albumReview" onChange={handleChange}></textarea>
        <input type="number" placeholder="Out of 10" onChange={handleChange} name="albumRating"></input> 
        <button type="submit">Add album</button>
    </form>
  )
}
