import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom';
import { addAlbum, getSearchedAlbums } from './util/api';
import AlbumListSearch from './components/AlbumListSearch';
export function Add() {
  const navigate = useNavigate();
  const [albumData, setAlbumData] = useState({
    albumReview: "",
    albumRating: null,
  });
  const [searchAlbumState, setSearchAlbumState] = useState([]);
  const [selectedAlbum, setSelectedAlbum] = useState(null);
  function handleChange(event){
    setAlbumData(prevState=>(
      {...prevState, [event.target.name]: event.target.value}
    ))
  }; 

  async function displaySearchAlbums(){
    const albums = await searchAlbums();
    setSearchAlbumState(albums.items);
  }
  async function searchAlbums(){
    try{
      const res = await getSearchedAlbums(albumData.albumName);
      return res.data
    }
    catch(err){
      console.log(err)
    }

  }

  async function handleClick(event){
    event.preventDefault();
    try{
      const body = {
        album: searchAlbumState[selectedAlbum],
        userInput: albumData
      }
      await addAlbum(body);
      navigate("/");
    }
    catch(err){
      console.log("Error");
    }
  }

  return (
    <form className="add-form" onSubmit={handleClick}>
      <div className='add-form-search'>
        <input onChange={handleChange} className="search-bar" type="text" placeholder="Enter album name..." name="albumName"></input>
        <button className="search-button"type="button" onClick={displaySearchAlbums}>Search Album!</button>
        {searchAlbumState.length >0 && <div className='search-albums'>
          {<AlbumListSearch searchAlbumState={searchAlbumState} selectedAlbum={selectedAlbum} setSelectedAlbum={setSelectedAlbum}/>}
        </div>}
      </div>
      <div className='add-form-add'>
        <textarea className='add-form-add-textarea' placeholder='Write a review!' type="text" name="albumReview" onChange={handleChange}></textarea>
        <input type="number" placeholder="Out of 10" onChange={handleChange} name="albumRating"></input> 
        <button className="add-button"type="submit">Add album</button>
      </div>
    </form>
  )
}
