import {Link} from "react-router-dom";
import axios from "axios";
export const Albums = () => {
    useEffect(()=>{
        const fetchAllAlbums = async () =>{
            try{
                const res = await axios.get("http://localhost:8800/albums")
                setAlbums(res.data);
            }
            catch(err){
                console.log(err);
            }
        }
        fetchAllAlbums();
    },[])



  return (
    <div>
        <h1>Album review thingy</h1>
        <div className="albums">
            {albums.map((album)=>(
                <div className="album" key={album.id}>
                    <img src={album.albumCover} alt=''/>
                    <h2>{album.albumName}</h2>
                    <h3>{album.albumArtist}</h3>
                    <p>{album.albumReview}</p>
                    <p><span>{album.albumRating}</span>/10</p>
                </div>
            ))}
        </div>
        <button><Link to="/add">Add new album</Link></button>
    </div>

  )
}
